class Eng_dict:
    def __init__(self, data):
        self.__generate(data)

    def __generate(self, data):
        self.__dct = {}

        for word in data:
            cur_dct = self.__dct
            for char in word:
                if char not in cur_dct:
                    cur_dct[char] = {}
                cur_dct = cur_dct[char]
            cur_dct['def'] = data[word]

    def lookup(self, keyword):
        cur_dct=self.__dct
        for char in keyword:
            cur_dct=cur_dct.get(char)
            if cur_dct is None:
                return None
        return cur_dct.get('def',None)

    def len_lookup(self, length):
        def dfs(dct,keyword):
            if len(keyword)<length:
                for char in dct:
                    if char!='def':
                        yield from dfs(dct[char],keyword+char)
            elif 'def' in dct:
                yield keyword
        yield from dfs(self.__dct,'')

