class Word_cookies(Problem):
    def __init__(self, dct, letters, lengths):
        self.dct = dct
        self.letters = letters
        # Define words, lengths for state
        self.words = [None] * len(lengths)
        self.lengths = lengths

    def get_state(self):
        Problem.get_state(self)
        return tuple(self.words)

    def is_goal(self):
        for word in words:
            if word is None or\
            self.dct.lookup(word) or\
            len(set(word)) != len(word):
                return False

            for letter in word:
                if letter not in self.letters:
                    return False
        return True

    def get_pos_actions(self):
        Problem.get_pos_actions(self)
        for letters in powerset(self.letters):
            if len(letters) < 2:
                continue
            for perm in itertools.permutations(letters):
                yield list(perm)

    def action(self, *actions):
        Problem.action(self, actions)
        for action in actions:
            if action not in self.get_pos_actions():
                continue

            input_word = ''.join(action)
            if input_word in self.words:
                    continue

            for i in range(0, len(self.words)):
                if self.lengths[i] == len(input_word) and\
                self.words[i] is None:
                    self.words[i] = input_word
                    break

