def r_func():
    seed = 0 
    result = []
    for i in game.lengths:
        temp = list(itertools.permutations(game.letters, i))
        hash_val = hash(str(seed) + str(i))
        random_index = hash_val % len(temp)
        result.append(''.join(temp[random_index]))
    return tuple(result)

def n_func(state):
