def solve(problem, max_restart, max_iter, max_h, h_func, r_func, n_func):
    count_restart=0
    while count_restart<max_restart:
        state=r_func()
        count_iter=0
        while count_iter<max_iter:
            if h_func(state)>=max_h:
                return state
            state=max(n_func(state),key=h_func)
            count_iter+=1
        count_restart+=1
    return None
