def is_win_line(board, pos, length):
    if pos == None:
        return
    y, x = pos[0], pos[1]
    h, w = len(board), len(board[0])
    # check hàng
    cnt = 0
    y1 = y
    while y1 >= 0:
        if board[x][y1] != None and board[x][y1] == board[x][y]:
            cnt += 1
            y1 -= 1
        else:
            break
    y2 = y
    while y2 < h:
        if board[x][y2] != None and board[x][y2] == board[x][y]:
            cnt += 1
            y2 += 1
        else:
            break
    if cnt - 1 >= length:
        return True

    # check cột
    cnt = 0
    x1 = x
    while x1 >= 0:
        if board[x1][y] != None and board[x1][y] == board[x][y]:
            cnt += 1
            x1 -= 1
        else:
            break
    x2 = x
    while x2 < w:
        if board[x2][y] != None and board[x2][y] == board[x][y]:
            cnt += 1
            x2 += 1
        else:
            break
    if cnt - 1 >= length:
        return True

    # check chéo 1  
    cnt = 0
    x1 = x
    y1 = y
    while x1 >= 0 and y1 < h:
        if board[x1][y1] != None and board[x1][y1] == board[x][y]:
            x1 -= 1
            y1 += 1
            cnt += 1
        else:
            break
    x1 = x
    y1 = y
    while x1 < w and y1 >= 0:
        if board[x1][y1] != None and board[x1][y1] == board[x][y]:
            x1 += 1
            y1 -= 1
            cnt += 1
        else:
            break
    if cnt - 1 >= length:
        return True

    # check chéo 2   
    cnt = 0
    x1 = x
    y1 = y
    while x1 < w and y1 < h:
        if board[x1][y1] != None and board[x1][y1] == board[x][y]:
            x1 += 1
            y1 += 1
            cnt += 1
        else:
            break
    x1 = x
    y1 = y
    while x1 >= 0 and y1 >= 0:
        if board[x1][y1] != None and board[x1][y1] == board[x][y]:
            x1 -= 1
            y1 -= 1
            cnt += 1
        else:
            break
    if cnt - 1 >= length:
        return True

    return False

