class Caro:
    def __init__(self, w, h, line_len):
        self.w = w
        self.h = h
        self.line_len = line_len
        self.__board = np.full((h, w), None).tolist()
        self.sym = 'x'

    def to_move(self):
        return self.sym

    def get_pos_actions(self):
        for y, row in enumerate(self.__board):
            for x, cell in enumerate(row):
                if cell is None:
                    yield x, y

    def action(self, actions):
        if self.is_terminal():
            return

        for action in actions:
            x, y = action
            if (x, y) not in self.get_pos_actions():
                return

            self.__board[y][x] = self.sym
            self.sym = 'o' if self.sym == 'x' else 'x'

    def get_state(self):
        return tuple(tuple(x) for x in self.__board)

    def is_terminal(self):
        board = self.get_state()
        for i in range(self.w):
            for j in range(self.h):
                if is_win_line(board, (i, j), self.line_len):
                    return True
        for i in range(self.w):
            for j in range(self.h):
                if board[i][j] == None:
                    return False
        return True

    def utility(self, player):
        board = self.get_state()
        for i,j in range(self.w,self.h):
            if is_win_line(board, (i, j), self.line_len):
                return 1 if player == self.sym else -1
        return 0

    def show(self):
        for row in self.__board:
            for cell in row:
                if cell is None:
                    print('[ ]', end='')
                else:
                    print(f'[{cell}]', end='')
            print()

    # (10, 10) <= figsize <= (15, 15)
    def show_imgs(self, figsize):
        fig, ax = plt.subplots(figsize=figsize)
        for i in range(self.h + 1):
            ax.axhline(y=i, xmin=0, xmax=self.w, color='black', linewidth=1)
        for j in range(self.w + 1):
            ax.axvline(x=j, ymin=0, ymax=self.h, color='black', linewidth=1)

        for i in range(self.h):
            for j in range(self.w):
                if self.__board[i][j] == 'x':
                    ax.plot(j + 0.5, i + 0.5, 'x', markersize=20, markeredgewidth=3, color='green')
                elif self.__board[i][j] == 'o':
                    ax.plot(j + 0.5, i + 0.5, 'o', markersize=20, markeredgewidth=3, markerfacecolor='none', color='black')

        ax.set_xticks([])
        ax.set_yticks([])
        ax.set_xlim(0, self.w)
        ax.set_ylim(0, self.h)
        ax.invert_yaxis()

        ax.text(0.5, 1.07, "X", transform=ax.transAxes, ha="center", fontsize=18)
        ax.text(-0.09, 0.5, "Y", transform=ax.transAxes, va="center", rotation=90, fontsize=18)

        for i in range(self.h):
            ax.text(i + 0.5, -0.1-self.w/60, str(i), transform=ax.transData, ha="center", fontsize=15)
            ax.text(i+0.5, -0.01, "|", color="black", ha="center", fontsize=7)

        for j in range(self.w):
            ax.text(-0.15-self.h/60, j + 0.5, str(j), transform=ax.transData, va="center", rotation=0, fontsize=15)
            ax.text(-0.05, j + 0.5,"-", color="black", va="center", rotation=0, fontsize=12)

