def solve(game):
    sim_game = copy.deepcopy(game)
    uti, best_a = max_value(sim_game)
    return best_a 

def max_value(sim_game):
    player = sim_game.to_move()
    if sim_game.is_terminal():
        return sim_game.utility(player), None
    max_uti, best_a = -float('inf'), None
    for action in sim_game.get_pos_actions():
        game_a = copy.deepcopy(sim_game)
        game_a.action([action])
        uti, a = min_value(game_a)
        if uti > max_uti:
            max_uti, best_a = uti, action
    return max_uti, best_a

def min_value(sim_game):
    player = sim_game.to_move()
    if sim_game.is_terminal():
        return sim_game.utility(player), None
    min_uti, best_a = float('inf'), None
    for action in sim_game.get_pos_actions():
        game_a = copy.deepcopy(sim_game)
        game_a.action([action])
        uti, a = max_value(game_a)
        if uti < min_uti:
            min_uti, best_a = uti, action
    return min_uti, best_a
